SourceCode.Declaration:
public class DEVIntegAICallStatistics
{
    public DEVSysTimer    timer;
    public int            tokenCount;
    public int            promptTokens;
    public int            candidateTokens;
    public static int     TIMER_CLOCK_NUM = 1;
}

Method.Source:
public void markStart()
{
    timer = new DEVSysTimer();
    tokenCount = 0;
    promptTokens = 0;
    candidateTokens = 0;
    timer.start(TIMER_CLOCK_NUM);
}

public real getExecutionTimeSeconds()
{
    real timeInMs = timer.time(TIMER_CLOCK_NUM);
    return timeInMs / 1000.0;
}

public void setTokenUsage(int _promptTokens, int _candidateTokens)
{
    promptTokens = _promptTokens;
    candidateTokens = _candidateTokens;
    tokenCount = _promptTokens + _candidateTokens;
}

public str getFormattedStatistics()
{
    str result;

    result = strFmt("Execution Time: %1 seconds\n", num2Str(this.getExecutionTimeSeconds(), 0, 1, 1, 1));
    result += strFmt("Total Tokens: %1\n", tokenCount);
    result += strFmt("Prompt Tokens: %1\n", promptTokens);
    result += strFmt("Response Tokens: %1\n", candidateTokens);

    return result;
}
