public Object dialog()
{
    dialog = super();
    dlgMessageTypeId.allowEdit(false);
    return dialog;
}

static ClassDescription description()
{
    return "Tutorial Import Purch Invoice PDF";
}

public static DEVIntegTutorialPurchOrderOCRManualImport construct()
{
    return new DEVIntegTutorialPurchOrderOCRManualImport();
}

public static void main(Args _args)
{
    DEVIntegTutorialPurchOrderOCRManualImport  runObject = DEVIntegTutorialPurchOrderOCRManualImport::construct();
    DEVIntegMessageTypeInbound         messageTypeInbound;
    ;
    //replace this with a separate table later
    select firstonly messageTypeInbound
        where messageTypeInbound.ClassName == classStr(DEVIntegTutorialPurchOrderOCRProcess);
    if (!messageTypeInbound)
    {
        throw error(strFmt("Unable to find message type for %1 class", classStr(DEVIntegTutorialPurchOrderOCRProcess)));
    }

    runObject.parmMessageTypeId(messageTypeInbound.MessageTypeId);

    if (runObject.prompt())
    {
        runObject.runOperation();

        FormDataSource          formDataSource = FormDataUtil::getFormDataSource(_args.record());

        if (formDataSource)
        {
            formDataSource.research(false);
            if (_args.dataset() == tableNum(DEVIntegMessageTable))
            {
                formDataSource.positionToRecord(runObject.getMessageTable());
            }
        }

    }
}
